DROP TABLE IF EXISTS `es_validator_platform`;
CREATE TABLE `es_validator_platform` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `name` varchar(100) DEFAULT NULL COMMENT '验证方式名称',
  `open` int(1) DEFAULT NULL COMMENT '是否开启 0：否，1：是',
  `config` longtext COMMENT '验证方式配置',
  `plugin_id` varchar(100) DEFAULT NULL COMMENT '验证方式插件ID',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

